$('#contact_sub').on('click', function(){
    var email = $('input[name="contact_email"]').val();
    var phone_number = $('input[name="contact_phone_number"]').val();
    var mobile_number = $('input[name="contact_mobile_number"]').val();

    bootbox.confirm({ 
        message: "Wirklich aktualisieren?", 
        callback: function(result){
            
            $.ajax({
                type: "POST",
                url: "js/php/SAVE_customer_data.php",
                data: {
                    AREA: contact_sub,
                    email: email,
                    phone_number: phone_number,
                    mobile_number: mobile_number
                },
                success: function(){
                    var place = "messenger-on-top messenger-on-right",
                        theme = "flat";

                    msgOpt(place, theme);
                    Messenger().post({
                        message: 'Die Kontaktdaten wurden gespeichert.',
                        type: 'success',
                        showCloseButton: true
                    });
                },
                error: function(){
                    bootbox.alert("Es ist ein Fehler beim Bearbeiten der Anfrage aufgetreten.\nBitte kontaktieren Sie den Administrator.");
                }
            });
        }
    })
});